package operator1;

public class Sample1 {

	public static void main(String[] args) {
		
		// TODO Auto-generated method stub
		int a =60,b = 30;
		System.out.println(a + " + " + b + " = " +(a+b));
		System.out.println(a + " - " + b + " = " +(a-b));
		System.out.println(a + " * " + b + " = " +(a*b));
		System.out.println(a + " / " + b + " = " +(a/b));
	}

}
